[HttpPost]\npublic ActionResult MyAction(DynamicFormViewModel model)\n{\n    for (int i = 0; i < model.Fields.Count; i++)\n    {\n        if (!ModelState.IsValidField(model.Fields[i].Value))\n        {\n            ModelState.AddModelError($"Fields[{i}].Value", "Invalid value");\n        }\n\n        if (!ModelState.IsValidField(model.Fields[i].Date))\n        {\n            ModelState.AddModelError($"Fields[{i}].Date", "Invalid date");\n        }\n    }\n\n    if (!ModelState.IsValid)\n    {\n        return View(model);\n    }\n\n    // Save data\n}