public class OneOfTwoFieldsValidator : BaseValidator\n{\n    private string _control1Id;\n    private string _control2Id;\n\n    public OneOfTwoFieldsValidator()\n    {\n        EnableClientScript = true;\n    }\n\n    public string Control1Id\n    {\n        get { return _control1Id; }\n        set { _control1Id = value; }\n    }\n\n    public string Control2Id\n    {\n        get { return _control2Id; }\n        set { _control2Id = value; }\n    }\n\n    protected override bool EvaluateIsValid()\n    {\n        string value1 = HttpContext.Current.Request.Form[Control1Id];\n        string value2 = HttpContext.Current.Request.Form[Control2Id];\n\n        return !string.IsNullOrEmpty(value1) || !string.IsNullOrEmpty(value2);\n    }\n\n    protected override void AddAttributesToRender(HtmlTextWriter writer)\n    {\n        base.AddAttributesToRender(writer);\n\n        writer.AddAttribute("evaluationfunction", "OneOfTwoFieldsValidator_ClientValidate");\n        writer.AddAttribute("control1id", Control1Id);\n        writer.AddAttribute("control2id", Control2Id);\n    }\n}