public class CustomWebResourceUrlProvider : WebResourceUrlProvider\n{\n  public override string GetUrl(string resourceName, string assemblyName)\n  {\n    // Custom logic to generate the URL based on the virtual path\n    string virtualPath = HttpContext.Current.Request.ApplicationPath;\n    if (virtualPath.StartsWith("/userid/websitename"))\n    {\n      return string.Format("{0}/{1}/{2}", virtualPath, "WebResource.axd", resourceName);\n    }\n    else\n    {\n      return base.GetUrl(resourceName, assemblyName);\n    }\n  }\n}