using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic partial class _Default : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        string connectionString = "Your database connection string";\n        string query = "SELECT YourColumnName FROM YourTableName";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                using (SqlDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        string itemValue = reader["YourColumnName"].ToString();\n                        Response.Write("<script>addItem('" + itemValue + "');</script>");\n                    }\n                }\n            }\n        }\n    }\n}