using System;\nusing System.Runtime.InteropServices;\nusing Microsoft.Win32.TaskScheduler;\n\nclass MyTask\n{\n    [DllImport("taskschd.dll")]\n    static extern void CreateTask(IntPtr handle, string taskName, string taskDescription, string executablePath);\n\n    public static void Main()\n    {\n        // Create a new task\n        using (TaskScheduler scheduler = new TaskScheduler())\n        {\n            TaskDefinition task = scheduler.CreateTaskDefinition("MyTask");\n            task.Description = "My custom task";\n            task.Actions.Add(new ExecAction("C:\\Path\\To\\MyTask.exe"));\n            scheduler.RootFolder.RegisterTaskDefinition(task);\n        }\n    }\n}