using System;\nusing System.Web;\n\npublic class RestHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        string verb = context.Request.HttpMethod;\n        string resource = context.Request.Url.AbsolutePath;\n\n        // Handle GET requests\n        if (verb == "GET")\n        {\n            // Return a resource\n            context.Response.Write("Hello, world!");\n        }\n        // Handle POST requests\n        else if (verb == "POST")\n        {\n            // Create a new resource\n            context.Response.Write("Resource created!");\n        }\n        // Handle other verbs...\n    }\n\n    public bool IsReusable\n    {\n        get { return true; }\n    }\n}