public class WordReader\n{\n    private string[] words;\n    private int index;\n\n    public WordReader(string data)\n    {\n        words = data.Split(new char[] { ' ', '\t', '\n', '\r' }, StringSplitOptions.RemoveEmptyEntries);\n        index = 0;\n    }\n\n    public string ReadWord()\n    {\n        if (index >= words.Length) return null;\n        return words[index++];\n    }\n}