using Oracle.DataAccess.Client;\n\n// Create a new OracleConnection object\nOracleConnection conn = new OracleConnection("Data Source=myDS;User Id=myUser;Password=myPassword;");\n\n// Open the connection with the specified rollback segment\nconn.Open(OracleConnectionOptions.UserSpecifiedRollbackSegment, "MY_ROLLBACK_SEGMENT");\n\n// Begin the transaction\nOracleTransaction trans = conn.BeginTransaction();\n\n// Perform your operations\n// ...\n\n// Commit or rollback the transaction\ntrans.Commit();