using PuppeteerSharp;\n\npublic async Task<byte[]> GetVideoSnapshotAsync(string youtubeVideoHtml)\n{\n    var browser = await Puppeteer.LaunchAsync(new LaunchOptions { Headless = true });\n    var page = await browser.NewPageAsync();\n    await page.SetContentAsync(youtubeVideoHtml);\n    await page.WaitForSelectorAsync("video");\n    var screenshot = await page.ScreenshotAsync(new ScreenshotOptions { Clip = await page.QuerySelectorAsync("video") });\n    await browser.CloseAsync();\n    return screenshot;\n}