// Managers.aspx.cs\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (User.IsInRole("Admin"))\n    {\n        // Load GridView with directors\n        GridViewDirectors.Visible = true;\n        GridViewProjectManagers.Visible = false;\n    }\n    else if (User.IsInRole("Director"))\n    {\n        // Load GridView with Project Managers belonging to this director\n        GridViewDirectors.Visible = false;\n        GridViewProjectManagers.Visible = true;\n        // Bind GridViewProjectManagers to data source\n    }\n}