using log4net;\n\npublic class PerformanceLogger\n{\n    private static readonly ILog _log = LogManager.GetLogger(typeof(PerformanceLogger));\n\n    public void LogPerformanceMetrics()\n    {\n        var memoryUsage = GC.GetTotalMemory(true);\n        var threadCount = Thread.CurrentThread.ManagedThreadId;\n        var queueLength = HttpContext.Current.ApplicationInstance.RequestQueue.QueueLength;\n\n        _log.InfoFormat("Memory usage: {0} MB, Thread count: {1}, Request queue length: {2}", memoryUsage / 1024 / 1024, threadCount, queueLength);\n    }\n}