using System.ServiceModel;\nusing TIBCO.EMS.Wcf;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Create a custom binding\n        CustomBinding binding = new CustomBinding();\n\n        // Add the EMS transport element\n        binding.Elements.Add(new EmstransportElement());\n\n        // Create a channel factory\n        ChannelFactory<IEMSChannel> factory = new ChannelFactory<IEMSChannel>(binding);\n\n        // Create a channel\n        IEMSChannel channel = factory.CreateChannel();\n\n        // Send a message using the channel\n        channel.Send("Hello, World!");\n    }\n}