// MyController.cs\npublic ActionResult GetHtml()\n{\n    MyModel model = new MyModel(); // populate your model data\n    PartialViewResult partialView = PartialView("MyPartialView", model);\n    string html = RenderPartialToString(partialView);\n    return Content(html, "text/html");\n}\n\n// Helper method to render PartialView to string\nprivate string RenderPartialToString(PartialViewResult partialView)\n{\n    using (StringWriter sw = new StringWriter())\n    {\n        ViewEngines.Engines.FindPartialView(ControllerContext, partialView.ViewName).Render(sw, null);\n        return sw.ToString();\n    }\n}