public class OrderedDictionary<TKey, TValue>\n{\n    private readonly LinkedList<TValue> _list = new LinkedList<TValue>();\n    private readonly Dictionary<TKey, LinkedListNode<TValue>> _dictionary = new Dictionary<TKey, LinkedListNode<TValue>>();\n\n    public void Add(TKey key, TValue value)\n    {\n        var node = _list.AddLast(value);\n        _dictionary[key] = node;\n    }\n\n    public TValue Get(TKey key)\n    {\n        return _dictionary[key].Value;\n    }\n\n    public IEnumerator<TValue> GetEnumerator()\n    {\n        return _list.GetEnumerator();\n    }\n}