using System;\nusing System.Security.Cryptography;\n\nclass Program\n{\n    static void Main()\n    {\n        byte[] data = System.Text.Encoding.UTF8.GetBytes("Hello, World!");\n        byte[] hash;\n\n        using (SHA256 sha256 = SHA256.Create())\n        {\n            hash = sha256.ComputeHash(data);\n        }\n\n        Console.WriteLine(BitConverter.ToString(hash));\n    }\n}