using Titanium.Web.Proxy;\nusing Titanium.Web.Proxy.Models;\n\nvar proxyServer = new ProxyServer();\n\n// setup the proxy server\nproxyServer.BeforeRequest += OnBeforeRequest;\nproxyServer.BeforeResponse += OnBeforeResponse;\n\n// start the proxy server\nproxyServer.Start();\n\n// example event handlers\nvoid OnBeforeRequest(object sender, SessionEventArgs e)\n{\n    // inspect or modify the request\n    var request = e.HttpClient.Request;\n    Console.WriteLine($"Request: {request.Method} {request.Url}");\n}\n\nvoid OnBeforeResponse(object sender, SessionEventArgs e)\n{\n    // inspect or modify the response\n    var response = e.HttpClient.Response;\n    Console.WriteLine($"Response: {response.StatusCode}");\n}