using FiddlerCore;\n\nclass HttpInterceptor : IFiddlerHttpInspector\n{\n    public void OnBeforeRequest(Session oSession)\n    {\n        // Modify the request if needed\n    }\n\n    public void OnBeforeResponse(Session oSession)\n    {\n        // Modify the response if needed\n    }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Initialize FiddlerCore\n        FiddlerApplication.Startup(0, FiddlerCoreStartupFlags.Registry);\n\n        // Create an instance of the HttpInterceptor\n        var inspector = new HttpInterceptor();\n\n        // Register the inspector with FiddlerCore\n        FiddlerApplication.BeforeRequest += inspector.OnBeforeRequest;\n        FiddlerApplication.BeforeResponse += inspector.OnBeforeResponse;\n    }\n}