string myExecutable = "myApp.exe";\nstring server = "myServer";\nstring database = "top_secret_data";\nstring userID = "myUser";\nstring password = "ABC123";\n\n// Encrypt sensitive data\nvar crypto = new MySymmetricCryptoLib.Crypto();\nstring encryptedServer = crypto.Encrypt(server);\nstring encryptedDatabase = crypto.Encrypt(database);\nstring encryptedUserID = crypto.Encrypt(userID);\nstring encryptedPassword = crypto.Encrypt(password);\n\n// Set environment variables\nEnvironment.SetEnvironmentVariable("DB_SERVER", encryptedServer);\nEnvironment.SetEnvironmentVariable("DB_DATABASE", encryptedDatabase);\nEnvironment.SetEnvironmentVariable("DB_USER_ID", encryptedUserID);\nEnvironment.SetEnvironmentVariable("DB_PASSWORD", encryptedPassword);\n\n// Start the process\nProcessStartInfo startInfo = new ProcessStartInfo(myExecutable);\nProcess.Start(startInfo);