using Microsoft.Synchronization.Files;\n\n// Create a file sync provider\nFileSyncProvider provider = new FileSyncProvider();\n\n// Set the source and destination directories\nprovider.SourceDirectory = @"C:\Source";\nprovider.DestinationDirectory = @"C:\Destination";\n\n// Set the synchronization options\nFileSyncOptions options = new FileSyncOptions();\noptions.Recursive = true;\n\n// Synchronize the directories\nFileSyncResult result = provider.Synchronize(options);