public class MultiScrollBarGrid : DataGridView\n{\n    private int _numParts = 3; // Number of parts to divide the grid into\n    private int _partWidth = 200; // Width of each part\n\n    protected override void OnPaint(PaintEventArgs e)\n    {\n        base.OnPaint(e);\n\n        // Calculate the width of each part\n        int partWidth = _partWidth;\n\n        // Draw the scrollbars for each part\n        for (int i = 0; i < _numParts; i++)\n        {\n            Rectangle partRect = new Rectangle(i * partWidth, 0, partWidth, this.ClientSize.Height);\n            Rectangle scrollbarRect = new Rectangle(partRect.Right - 20, partRect.Top, 20, partRect.Height);\n\n            // Draw the scrollbar\n            e.Graphics.FillRectangle(Brushes.Gray, scrollbarRect);\n            e.Graphics.DrawRectangle(Pens.Black, scrollbarRect);\n        }\n    }\n\n    protected override void OnMouseDown(MouseEventArgs e)\n    {\n        base.OnMouseDown(e);\n\n        // Handle scrollbar clicks\n        for (int i = 0; i < _numParts; i++)\n        {\n            Rectangle partRect = new Rectangle(i * _partWidth, 0, _partWidth, this.ClientSize.Height);\n            Rectangle scrollbarRect = new Rectangle(partRect.Right - 20, partRect.Top, 20, partRect.Height);\n\n            if (scrollbarRect.Contains(e.Location))\n            {\n                // Handle scrollbar click for this part\n                Console.WriteLine($"Scrollbar {i} clicked");\n            }\n        }\n    }\n}