private void PrintButton_Click(object sender, RoutedEventArgs e)\n{\n    string selectedFile = fileComboBox.SelectedItem as string;\n    if (!string.IsNullOrEmpty(selectedFile))\n    {\n        // Determine the file type and print accordingly\n        if (selectedFile.EndsWith(".pdf", StringComparison.OrdinalIgnoreCase))\n        {\n            PrintPdf(selectedFile);\n        }\n        else if (selectedFile.EndsWith(".docx", StringComparison.OrdinalIgnoreCase))\n        {\n            PrintWordDoc(selectedFile);\n        }\n        // Add more file type checks as needed\n    }\n}