public class Speaker\n{\n    // ...\n\n    public Speaker()\n    {\n        Tweeter tweeter = new Tweeter();\n        tweeter.SpeakToMe += Tweeter_SpeakToMe;\n    }\n\n    private void Tweeter_SpeakToMe(object sender, SpeakToMeEventArgs e)\n    {\n        Console.Out.WriteLine("the tweeter is speaking: " + e.Message);\n        OnSpeakToMe("tweeter rockin' out [" + e.Message + "]");\n    }\n\n    protected virtual void OnSpeakToMe(string message)\n    {\n        if (SpeakToMe != null) SpeakToMe(this, new SpeakToMeEventArgs(DateTime.Now.ToString() + " - " + message));\n    }\n}\n\npublic class WinForm\n{\n    private Speaker speaker;\n\n    public WinForm()\n    {\n        speaker = new Speaker();\n        speaker.SpeakToMe += Speaker_SpeakToMe;\n    }\n\n    private void Speaker_SpeakToMe(object sender, SpeakToMeEventArgs e)\n    {\n        Console.Out.WriteLine("Received message from speaker: " + e.Message);\n    }\n}