public class NullableDateTimePicker : DateTimePicker\n{\n    private CheckBox nullCheckBox = new CheckBox();\n\n    public NullableDateTimePicker()\n    {\n        nullCheckBox.Text = "Null";\n        nullCheckBox.CheckedChanged += NullCheckBox_CheckedChanged;\n        this.Controls.Add(nullCheckBox);\n    }\n\n    private void NullCheckBox_CheckedChanged(object sender, EventArgs e)\n    {\n        this.Enabled = !nullCheckBox.Checked;\n        if (nullCheckBox.Checked)\n        {\n            this.Value = DateTime.MinValue;\n        }\n    }\n\n    public DateTime? NullableValue\n    {\n        get\n        {\n            if (nullCheckBox.Checked)\n            {\n                return null;\n            }\n            else\n            {\n                return this.Value;\n            }\n        }\n        set\n        {\n            if (value.HasValue)\n            {\n                nullCheckBox.Checked = false;\n                this.Value = value.Value;\n            }\n            else\n            {\n                nullCheckBox.Checked = true;\n            }\n        }\n    }\n}