using System;\nusing System.Net.Mail;\nusing SendGrid;\n\n// ...\n\n// Create a new instance of the SendGrid API\nvar sendGridClient = new SendGridClient("YOUR_API_KEY");\n\n// Create a new message\nvar message = new SendGridMessage();\nmessage.Subject = "Test Email";\nmessage.HtmlContent = "This is a test email";\n\n// Send the message\nsendGridClient.SendEmailAsync(message).Wait();