using Oracle.ManagedDataAccess.Client;\n\n// Create the OracleCommand object\nOracleCommand cmd = new OracleCommand("EXECUTE_UPDATE", connection);\ncmd.CommandType = CommandType.StoredProcedure;\n\n// Create the parameters\nOracleParameter pValue = new OracleParameter("p_value", OracleDbType.Int32, ParameterDirection.Input);\npValue.Value = 10;\n\nOracleParameter pIds = new OracleParameter("p_ids", OracleDbType.Array, ParameterDirection.Input);\npIds.UdtTypeName = "NUMBER_TABLE";\npIds.Value = new int[] { 1, 2, 3, 4, 5 };\n\n// Add the parameters to the command\ncmd.Parameters.Add(pValue);\ncmd.Parameters.Add(pIds);\n\n// Execute the procedure\nconnection.Open();\nint result = (int)cmd.ExecuteScalar();\nconnection.Close();