using System;\nusing System.Web;\n\npublic class StaticFileAuthorizationModule : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.BeginRequest += new EventHandler(OnBeginRequest);\n    }\n\n    void OnBeginRequest(object sender, EventArgs e)\n    {\n        HttpApplication app = (HttpApplication)sender;\n        HttpRequest request = app.Request;\n\n        // Check if the request is for a static file\n        if (request.Url.AbsolutePath.EndsWith(".jpg", StringComparison.OrdinalIgnoreCase) ||\n            request.Url.AbsolutePath.EndsWith(".css", StringComparison.OrdinalIgnoreCase) ||\n            request.Url.AbsolutePath.EndsWith(".js", StringComparison.OrdinalIgnoreCase))\n        {\n            // Check if the user is authenticated and authorized\n            if (!app.User.Identity.IsAuthenticated || !app.User.IsInRole("Developers"))\n            {\n                // Return a 401 Unauthorized response\n                app.Response.StatusCode = 401;\n                app.Response.StatusDescription = "Unauthorized";\n                app.Response.End();\n            }\n        }\n    }\n\n    public void Dispose()\n    {\n        // Nothing to dispose\n    }\n}