// C# example using SQL Server Broker\nusing System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string connectionString = "Data Source=your_server;Initial Catalog=your_database;Integrated Security=True";\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            // Create a message type\n            SqlCommand command = new SqlCommand("CREATE MESSAGE TYPE [my_message_type] AUTHORIZATION [dbo] VALIDATION = NONE", connection);\n            command.ExecuteNonQuery();\n\n            // Create a contract\n            command = new SqlCommand("CREATE CONTRACT [my_contract] AUTHORIZATION [dbo] (my_message_type SENT BY INITIATOR)", connection);\n            command.ExecuteNonQuery();\n\n            // Send a message to the remote machine\n            command = new SqlCommand("SEND ON CONVERSATION 'my_conversation' MESSAGE TYPE [my_message_type] ('arg1', 'arg2')", connection);\n            command.ExecuteNonQuery();\n        }\n    }\n}