using Microsoft.Win32;\n\n// Create a registry key for the file type association\nRegistryKey fileTypeKey = Registry.ClassesRoot.CreateSubKey(".lala");\n\n// Set the file type association to your application\nfileTypeKey.SetValue("", "LalaFile");\nRegistryKey lalaFileKey = Registry.ClassesRoot.CreateSubKey("LalaFile");\nlalaFileKey.CreateSubKey("shell").CreateSubKey("open").CreateSubKey("command").SetValue("", "\"" + System.Reflection.Assembly.GetExecutingAssembly().Location + "\" \"%1\"");\n\n// To restore the default association, simply delete the registry key\n// Registry.ClassesRoot.DeleteSubKey(".lala");