using System;\nusing System.Threading;\n\nclass ConditionSynchronizationExample\n{\n    private readonly object _lock = new object();\n    private bool _conditionMet = false;\n\n    public void WaitUntilConditionIsMet()\n    {\n        lock (_lock)\n        {\n            while (!_conditionMet)\n            {\n                Monitor.Wait(_lock);\n            }\n        }\n    }\n\n    public void SetCondition()\n    {\n        lock (_lock)\n        {\n            _conditionMet = true;\n            Monitor.Pulse(_lock);\n        }\n    }\n}