protected void Page_Init(object sender, EventArgs e)\n{\n    // Get the session state store\n    var sessionStateStore = new SqlSessionStateStore();\n\n    // Get the current session ID\n    var sessionId = HttpContext.Current.Session.SessionID;\n\n    // Get the session state from the store\n    var sessionState = sessionStateStore.GetItemExclusive(sessionId, out var lockId, out var item);\n\n    // Set the session state for the current request\n    HttpContext.Current.Session = sessionState;\n}