// Shared data contract\npublic class DataItem\n{\n    public string Id { get; set; }\n    public string Value { get; set; }\n    public int Version { get; set; }\n}\n\n// Server-side code (e.g., WCF service)\npublic class SyncService\n{\n    private readonly SyncProvider _provider;\n\n    public SyncService()\n    {\n        _provider = new SyncProvider(new DataStore());\n    }\n\n    public void Sync(DataItem[] clientData)\n    {\n        // Get server data\n        DataItem[] serverData = _provider.GetData();\n\n        // Sync data\n        SyncResult result = _provider.Sync(clientData, serverData);\n\n        // Return updated data\n        return result.UpdatedData;\n    }\n}\n\n// Client-side code (Silverlight)\npublic class SyncClient\n{\n    private readonly SyncAgent _agent;\n\n    public SyncClient()\n    {\n        _agent = new SyncAgent(new DataStore());\n    }\n\n    public void Sync()\n    {\n        // Get local data\n        DataItem[] clientData = _agent.GetData();\n\n        // Sync with server\n        DataItem[] updatedData = SyncService.Sync(clientData);\n\n        // Update local data\n        _agent.UpdateData(updatedData);\n    }\n}