using System;\nusing System.Runtime.InteropServices;\n\npublic class ProcessRenamer\n{\n    [DllImport("user32")]\n    private static extern bool SetWindowText(IntPtr hWnd, string lpString);\n\n    public static void RenameProcessWindow(IntPtr hWnd, string newTitle)\n    {\n        if (!SetWindowText(hWnd, newTitle))\n        {\n            throw new Exception("Failed to rename process window");\n        }\n    }\n}