using System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private PluginLoader _pluginLoader;\n\n    public MainForm()\n    {\n        _pluginLoader = new PluginLoader(@"C:\Plugins");\n        InitializeComponent();\n    }\n\n    private void InitializeTabs()\n    {\n        foreach (IModule module in _pluginLoader.LoadModules())\n        {\n            TabPage tabPage = new TabPage(module.Name);\n            tabPage.Controls.Add(new ModuleControl(module));\n            tabControl1.TabPages.Add(tabPage);\n        }\n    }\n}