using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing HtmlAgilityPack;\n\nnamespace WebScraper\n{\n    public class WebScraper\n    {\n        private readonly HttpClient _httpClient;\n\n        public WebScraper()\n        {\n            _httpClient = new HttpClient();\n        }\n\n        public async Task ExtractDataAsync(string url)\n        {\n            // Send HTTP request and get HTML response\n            var response = await _httpClient.GetAsync(url);\n            response.EnsureSuccessStatusCode();\n\n            // Parse HTML document\n            var htmlDoc = new HtmlDocument();\n            htmlDoc.LoadHtml(await response.Content.ReadAsStringAsync());\n\n            // Extract data using XPath or CSS selectors\n            var data = htmlDoc.DocumentNode.SelectSingleNode("//div[@class='example']");\n\n            // Process extracted data\n            Console.WriteLine(data.InnerText.Trim());\n        }\n    }\n}