public class TokenAuthenticator\n{\n    public string GenerateToken(int userId)\n    {\n        // Generate a token that expires after 20 minutes\n        var token = new Token\n        {\n            UserId = userId,\n            ExpiresAt = DateTime.Now.AddMinutes(20)\n        };\n\n        // Store the token in a secure storage\n        // ...\n\n        return token.TokenValue;\n    }\n\n    public bool ValidateToken(string tokenValue)\n    {\n        // Retrieve the token from the secure storage\n        // ...\n\n        if (token != null && token.ExpiresAt > DateTime.Now)\n        {\n            // Token is valid, refresh it if necessary\n            // ...\n            return true;\n        }\n\n        return false;\n    }\n}