using Microsoft.Practices.Unity;\nusing Microsoft.Practices.Unity.Configuration;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Load the Unity container from the XML file\n        IUnityContainer container = new UnityContainer();\n        container.LoadConfiguration();\n\n        // Get all instances of ICalculationPlugin\n        IEnumerable<ICalculationPlugin> plugins = container.ResolveAll<ICalculationPlugin>();\n\n        // Get the two numbers from the user\n        double num1 = double.Parse(Console.ReadLine());\n        double num2 = double.Parse(Console.ReadLine());\n\n        // Execute all plugins and display the results\n        foreach (ICalculationPlugin plugin in plugins)\n        {\n            double result = plugin.Calculate(num1, num2);\n            Console.WriteLine($"Result from {plugin.GetType().Name}: {result}");\n        }\n    }\n}