public enum ValueState {\n    UNKNOWN,\n    NULL,\n    KNOWN\n}\n\npublic class Value<T> {\n    private T value;\n    private ValueState state;\n\n    public Value(T value, ValueState state) {\n        this.value = value;\n        this.state = state;\n    }\n\n    public ValueState getState() {\n        return state;\n    }\n\n    public T getValue() {\n        return value;\n    }\n}