public event EventHandler MyEvent;\n\nprotected virtual void OnMyEvent(EventArgs e)\n{\n    // Get a copy of the event handlers to avoid race conditions\n    EventHandler handlers = MyEvent;\n\n    if (handlers != null)\n    {\n        // Use a thread pool thread to invoke the event handlers\n        ThreadPool.QueueUserWorkItem(state =>\n        {\n            handlers(this, e);\n        });\n    }\n}