using System.Transactions;\n\npublic class BusinessLogicLayer\n{\n    public void LongRunningProcess()\n    {\n        using (var scope = new TransactionScope(TransactionScopeOption.RequiresNew))\n        {\n            try\n            {\n                var dal = new DataAccessLayer();\n\n                // Call multiple DAL methods within the transaction scope\n                var result1 = dal.Method1();\n                if (result1 == true)\n                {\n                    var result2 = dal.Method2();\n                    if (result2 == true)\n                    {\n                        var result3 = dal.Method3();\n                        // ...\n                    }\n                }\n\n                // Commit the transaction only if all DAL methods succeed\n                scope.Complete();\n            }\n            catch (Exception ex)\n            {\n                // Rollback the transaction if any DAL method fails\n                scope.Dispose();\n                throw;\n            }\n        }\n    }\n}