var usersWithoutTerritories = from u in db.Users\n                             join uXt in db.User_x_Territories on u.Id equals uXt.UserID into g\n                             from user in g.DefaultIfEmpty()\n                             group user by new { u.Id, u.FirstName, u.LastName } into groupedUsers\n                             where groupedUsers.Count() == 0\n                             select new { Id = groupedUsers.Key.Id, FirstName = groupedUsers.Key.FirstName, LastName = groupedUsers.Key.LastName };