var groupedData = from row in dt.AsEnumerable()\n                  group row by row.Field<string>("OfficeName") into g\n                  select new\n                  {\n                      OfficeName = g.Key,\n                      Doctors = string.Join(", ", g.Select(r => r.Field<string>("DoctorName")))\n                  };\n\nRepeater1.DataSource = groupedData;\nRepeater1.DataBind();