// assuming 'dt' is your DataTable with the doctor office data\n\nDataView dv = new DataView(dt);\ndt = dv.ToTable(true, "OfficeName"); // group by OfficeName\n\n// create a new DataTable to hold the aggregated data\nDataTable aggregatedDt = new DataTable();\naggregatedDt.Columns.Add("OfficeName");\naggregatedDt.Columns.Add("Doctors");\n\n// iterate through the grouped data\nforeach (DataRow row in dt.Rows)\n{\n    string officeName = row["OfficeName"].ToString();\n    DataRow[] doctors = dt.Select("OfficeName = '" + officeName + "'");\n\n    // aggregate the doctors for each office\n    string doctorsList = string.Join(", ", doctors.Select(dr => dr["DoctorName"].ToString()));\n\n    // add the aggregated data to the new DataTable\n    aggregatedDt.Rows.Add(officeName, doctorsList);\n}\n\n// bind the Repeater to the aggregated data\nRepeater1.DataSource = aggregatedDt;\nRepeater1.DataBind();