// Service contract (decoupled from web)\n[ServiceContract]\npublic interface IFavoriteMovie\n{\n    [OperationContract]\n    MyFavoriteMovie GetItem();\n}\n\n// Service implementation\npublic class FavoriteMovieService : IFavoriteMovie\n{\n    public MyFavoriteMovie GetItem()\n    {\n        // Implementation here\n    }\n}\n\n// Hosting contract (web-specific)\n[ServiceContract]\npublic interface IFavoriteMovieWeb\n{\n    [OperationContract]\n    [WebGet]\n    MyFavoriteMovie GetItem();\n}\n\n// Web hosting implementation\npublic class FavoriteMovieWebService : IFavoriteMovieWeb\n{\n    private readonly IFavoriteMovie _service;\n\n    public FavoriteMovieWebService(IFavoriteMovie service)\n    {\n        _service = service;\n    }\n\n    public MyFavoriteMovie GetItem()\n    {\n        return _service.GetItem();\n    }\n}