using System;\nusing System.Xml;\nusing EnvDTE;\n\nclass ProjectFileExporter\n{\n    static void Main(string[] args)\n    {\n        // Create a new instance of the Visual Studio automation object\n        DTE dte = new DTE();\n\n        // Open the solution\n        dte.Solution.Open("PathToYourSolution.sln");\n\n        // Get the active solution\n        Solution solution = dte.Solution;\n\n        // Create an XmlDocument to store the project files\n        XmlDocument xmlDoc = new XmlDocument();\n        XmlElement root = xmlDoc.CreateElement("ProjectFiles");\n        xmlDoc.AppendChild(root);\n\n        // Iterate through all projects in the solution\n        foreach (Project project in solution.Projects)\n        {\n            // Iterate through all project items (files) in the project\n            foreach (ProjectItem projectItem in project.ProjectItems)\n            {\n                // Get the file name\n                string fileName = projectItem.Name;\n\n                // Create an XmlElement for the file\n                XmlElement fileElement = xmlDoc.CreateElement("File");\n                fileElement.SetAttribute("Name", fileName);\n                root.AppendChild(fileElement);\n            }\n        }\n\n        // Save the XmlDocument to a file\n        xmlDoc.Save("ProjectFiles.xml");\n\n        // Cleanup\n        dte.Quit();\n    }\n}