using OpenCvSharp;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new VideoCapture object\n        var capture = new VideoCapture(0); // 0 is the default camera index\n\n        // Check if the camera is opened\n        if (!capture.IsOpened())\n        {\n            Console.WriteLine("Cannot open camera");\n            return;\n        }\n\n        // Create a new Mat object to store the frame\n        var frame = new Mat();\n\n        // Read a frame from the camera\n        capture.Read(frame);\n\n        // Save the frame to an image file\n        frame.ImSave("image.jpg");\n\n        // Release the resources\n        frame.Dispose();\n        capture.Release();\n    }\n}