[TestFixture]\npublic class MapinfoWindowHandleTests\n{\n    [Test]\n    public void Constructor_SetsHandleField()\n    {\n        // Arrange\n        IntPtr expectedHandle = new IntPtr(100);\n\n        // Act\n        MapinfoWindowHandle sut = new MapinfoWindowHandle(expectedHandle);\n\n        // Assert\n        Assert.AreEqual(expectedHandle, sut.handle);\n    }\n\n    [Test]\n    public void HandleProperty_ReturnsExpectedValue()\n    {\n        // Arrange\n        IntPtr expectedHandle = new IntPtr(100);\n        MapinfoWindowHandle sut = new MapinfoWindowHandle(expectedHandle);\n\n        // Act\n        IntPtr result = ((IWin32Window)sut).Handle;\n\n        // Assert\n        Assert.AreEqual(expectedHandle, result);\n    }\n}