using Epson.ePOSPrint;\n\nclass ReceiptPrinter\n{\n    public void PrintReceipt(string printerName, string receiptData)\n    {\n        // Create a printer object\n        Printer printer = new Printer(printerName);\n\n        // Set the printer settings\n        printer.Settings.PaperSize = PaperSize.Receipt;\n        printer.Settings.Orientation = Orientation.Portrait;\n\n        // Print the receipt data\n        printer.Print(receiptData);\n\n        // Cut the paper\n        printer.CutPaper();\n    }\n}