SmtpClient client = new SmtpClient();\nclient.Host = "smtp.server.com";\nclient.Credentials = new System.Net.NetworkCredential("user", "password");\n\nMailAddress mailFrom = new MailAddress("mymail@server.com");\nMailAddress mailTo = new MailAddress("yourmail@server.com");\nMailAddress mailReply = new MailAddress("mymail@server.com");\n\nMailMessage message = new MailMessage(mailFrom, mailTo);\nmessage.Subject = "Test Email from C#";\nmessage.SubjectEncoding = System.Text.Encoding.UTF8;\n\n// Include a plain text version of the email\nmessage.Body = "This is a test message.";\nmessage.BodyEncoding = System.Text.Encoding.UTF8;\nmessage.IsBodyHtml = false;\n\n// Optionally, you can include an HTML version of the email\n// message.AlternateViews.Add(AlternateView.CreateAlternateViewFromString("<html><body>This is a test message.</body></html>", null, MediaTypeNames.Text.Html));\n\nclient.Send(message);