using System.Security.Cryptography;\nusing System.Text;\n\nprivate bool VerifyChecksum(int reqNumber, string reqText, string clientPassword, string reqChecksum)\n{\n    var hmac = new HMACSHA256(Encoding.UTF8.GetBytes(clientPassword));\n    var inputBytes = Encoding.UTF8.GetBytes($"{reqNumber}{reqText}");\n    var hashBytes = hmac.ComputeHash(inputBytes);\n    var calculatedChecksum = Convert.ToBase64String(hashBytes);\n    return calculatedChecksum.Equals(reqChecksum, StringComparison.Ordinal);\n}