using Microsoft.SharePoint.Client;\nusing Microsoft.SharePoint.Client.Workflow;\n\n// Set up your SharePoint site and credentials\nClientContext ctx = new ClientContext("https://your-site.sharepoint.com");\nctx.Credentials = new SharePointOnlineCredentials("username", "password");\n\n// Get the list and item\nList list = ctx.Web.Lists.GetByTitle("Your List Title");\nListItem item = list.GetItemById(1); // Replace with your item ID\n\n// Get the workflow instance\nWorkflowInstanceCollection instances = ctx.Web.GetWorkflowInstances(item);\nctx.Load(instances);\nawait ctx.ExecuteQueryAsync();\n\n// Find the workflow instance you want to stop\nforeach (var instance in instances)\n{\n    if (instance.Status == WorkflowStatus.InProgress)\n    {\n        // Stop the workflow instance\n        instance.Cancel();\n        await ctx.ExecuteQueryAsync();\n        break;\n    }\n}